import { Component } from '@angular/core';

@Component({
  selector: 'app-banner',
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.scss']
})
export class BannerComponent {
  name: string = 'Mi Banner';
  urlImg: string = 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fsp.depositphotos.com%2Fstock-photos%2Fpaisajes-de-suiza.html&psig=AOvVaw04BZtoATmikjb8xwCef_t-&ust=1680267198324000&source=images&cd=vfe&ved=0CA8QjRxqFwoTCLCMpp3Zg_4CFQAAAAAdAAAAABAE'
  photo: string = `url('${this.urlImg}')`;
}
